package com.data;

public class pattern3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n=5;
		for (int i = 0; i < 5; i++) {
		
			for (int j = 0; j < n; j++) {
			
				System.out.print("*");
			}
			
			n--;
			System.out.println();
			
		}
		
		
		
		
	}

}
