package interview;

public class evenorodd {

	
	public void m1(int i)
	{
	if(i%2==0)
	{
		System.out.println(i +" is even");
	}
	else
	{
		System.out.println(i +" is odd");
		
	}	
	}
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub

	evenorodd e=new evenorodd();
	e.m1(77);
	
	}

}
