package com.programs;

public class pattern6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int b=1,x=1;
		for (int i = 0; i < 5; i++) {
			
			for (int j = 0; j < b; j++) {
				
				System.out.print(x +" ");
				x++;
			}
			System.out.println();
			b++;
		}
		
		
	}

}
